import React, { Component } from 'react';
import {browserHistory} from 'react-router';
import DataDepto from '../data/depto.json';

class Departamento extends Component {

    onNavigateHome(){
        browserHistory.push('/inicio');
    }

    onFilter(){
      DataDepto = this.DataDepto.filter((obj, pos, arr) => {
      return arr.map(mapObj =>
        mapObj.codigo).indexOf(obj.codigo) === pos;
      });
    }

    render () {
        return (
            <div>
                <h3>Los Departamentos</h3>
                <p>C贸digos: </p>

                <table>
                <thead>
                  <tr>
                    <th>C贸digo</th>
                    <th>Nombre</th>
                    <th>C贸digo Padre</th>
                    <th>Descripci贸n Padre</th>
                  </tr>
                </thead>
                <tbody>
                    {DataDepto.map(function(deptoDetail, key) {
                    return (
                      <tr key = {key}>
                        <td>{deptoDetail.codigo}</td>
                        <td>{deptoDetail.nombre}</td>
                      </tr>
                    )
                    })}
                </tbody>
                </table>


                <button
                  onClick={this.onFilter}
                  className="btn btn-success"
                >
                  Filtrar
                </button>

                <button
                  onClick={this.onNavigateHome}
                  className="btn btn-primary"
                >
                    Volver
                </button>
            </div>
        )
    }
}

export default Departamento;
